mapper.select:
select p.*,
       case
           when l.liked is null then 0
           when l.liked then 1
           else -1 end   as liked,
       case
           when s.id is null then false
           else true end as saved
from forum.post p
         left join forum.saved_post s on s.post_id = p.id and s.uid = #{uid}
         left join forum.liked_post l on l.post_id = p.id and l.uid = #{uid}
where p.uid = #{uid}
and p.status = 0
order by ${ordering}
limit #{count} offset #{offset}

select p.*,
       case
           when l.liked is null then 0
           when l.liked then 1
           else -1 end   as liked,
       case
           when s.id is null then false
           else true end as saved
from forum.post p
         left join forum.saved_post s on s.post_id = p.id and s.uid = #{uid}
         left join forum.liked_post l on l.post_id = p.id and l.uid = #{uid}
where p.parent_id = #{parentId}
and p.status = 0
order by ${ordering}
limit #{count} offset #{offset}

mapper.update:
update forum.post
set pinned = not pinned
where id = #{id}
